INSERT INTO `free_kindle_cn_db_items` VALUES(479,'B008CVIKV0','杨澜:一问一世界','http://www.amazon.cn/%E6%9D%A8%E6%BE%9C-%E4%B8%80%E9%97%AE%E4%B8%80%E4%B8%96%E7%95%8C/dp/B008CVIKV0?SubscriptionId=AKIAITXIQMON7CH7D7YQ&tag=freekindlecn-23&linkCode=xm2&camp=2025&creative=165953&creativeASIN=B008CVIKV0','《杨澜:一问一世界》内容简介：杨澜入行20年的传记作品，立体展现杨澜20年非常媒体生涯，告诉你一个真实的杨澜和杨澜眼中的时代真相。在本书中，杨澜将告诉你：她从1990年到2010年的生命跨越和岁月流转；她与基辛格、克林顿、布莱尔、老布什、李光耀、韦尔奇等高端人物的交往秘辛；她如何在人生每个节点自由潇洒地转换；她对“赢”的重新理解和体悟；她的20年非常媒体生涯和她眼中的传媒江湖……','http://ec4.images-amazon.com/images/I/41vLrYfT4%2BL.jpg',NULL,'杨澜','江苏人民出版社',275,'2011-04-01','2012-12-13','2013-06-18T02:05:00+10:00','2013-06-23T04:03:44+10:00');
